import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import Book from "./components/book.component";

export default class BooksList extends Component {

    constructor(props) {
        super(props);

        this.deleteBook = this.deleteBook.bind(this);
        //this.onChangeTitle = this.onChangeTitle.bind(this);
        //this.onChangeAuthor = this.onChangeAuthor.bind(this);
        //this.onChangeYear = this.onChangeYear.bind(this);
        //this.onChangeIsbn = this.onChangeIsbn.bind(this);
        //this.onChangeCopies = this.onChangeCopies.bind(this);
        //this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            books: []
        }
    }

    componentDidMount() {
        Axios.get('http://localhost:5000/books/')
            .then(response => {
                    this.setState ({ books: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    deleteBook(id) {
        Axios.delete('http://localhost:5000/books/delete/'+id)
            .then(res => console.log(res.data));

            //Might need to make other function/ see 1:35:45
            //To filter 0 copies
        this.setState ({
            books: this.state.books.filter(el => el.copies !== 0)
        })
    }

    bookList() {
        return this.state.books.map(currentbook => {
            return <Book book={currentbook} deleteBook={this.deleteBook} key={currentbook._id}/>;
        })
    }

    render() {     
        return (
            <div>
                <h3>Books!</h3>
                <table className='table'>
                    <thead className='thead-light'>
                        <tr>
                            <th>Title</th>
                            <th>Author</th>
                            <th>Year of Publition</th>
                            <th>ISBN</th>
                            <th>Copies</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                        // this method takes information from books array and puts it into the rows
                        }
                        { this.bookList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
