import React from 'react';
import Table from 'react-bootstrap/Table';
import { Link } from 'react-router-dom';
import Axios from 'axios';
//SAME AS BOOK COMPONENT
const Posts = ({ posts, loading }) => {
  if (loading) {
    return <h2>Loading...</h2>;
  }

  const deleteBook = (id) => {
    Axios.delete('http://localhost:5000/books/delete/'+id)
        .then(res => console.log(res.data));

        //Might need to make other function/ see 1:35:45
        //To filter 0 copies

        //el.copies !== 0
    this.setState ({
        books: this.state.books.filter(el => el._id !== id)
    })
}

  return (
    <Table bordered striped hover>
      <thead className='thead-light'>
                        <tr>
                            <th >Image</th>
                            <th>Title</th>
                            <th>Author</th>
                            <th>Year of Publication</th>
                            <th>ISBN</th>
                            <th>Copies</th>
                        </tr>
                    </thead>
                    <tbody>
      {posts.map(post => (
        <tr key={post.id} className="bordered">

        {//<td> <img src= {post.image_url_m}> </img> </td>
        }
        <td><img src={post.image_url_s}  alt={post.title} /> </td>
        <td>{post.title}</td>
        <td>{post.author}</td>
        <td>{post.publication_year}</td>
        <td>{post.isbn}</td>
        <td>{post.copies}</td>
        <td>
            <Link to={"/show/"+post.book._id}>Details</Link> | <a href="#" onClick={() => { deleteBook(post.book._id) }}>Delete</a> | <a href="/" onClick={() => { post.returnBook(props.book._id) }}>Return</a> |<a href="#" onClick={() => { post.borrowBook(props.book._id) }}>Borrow</a> 
        </td>

    </tr>

      ))}
      </tbody>
    </Table>
  );
};

export default Posts;
