import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';

const Details = props => (
    <tr>
        <td>{props.book.title}</td>
        <td>{props.book.author}</td>
        <td>{props.book.publication_year}</td>
        <td>{props.book.isbn}</td>
        <td>{props.book.copies}</td>
        <td>
            <Link to={"/show/"+props.book._id}>Details</Link> | <a href="#" onClick={() => { props.deleteBook(props.book._id) }}>Delete</a> | <a href="#" onClick={() => { props.returnBook(props.book._id) }}>Return</a> |<a href="#" onClick={() => { props.borrowBook(props.book._id) }}>Borrow</a> 
        </td>
    </tr>
)

export default class Book extends Component {

    constructor(props) {
        super(props);
        //this.deleteBook = this.deleteBook.bind(this);
        //this.returnBook = this.returnBook.bind(this);
        //this.onChangeTitle = this.onChangeTitle.bind(this);
        //this.onChangeAuthor = this.onChangeAuthor.bind(this);
        //this.onChangeYear = this.onChangeYear.bind(this);
        //this.onChangeIsbn = this.onChangeIsbn.bind(this);
        //this.onChangeCopies = this.onChangeCopies.bind(this);
        //this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            book: []

        }
    }

    componentDidMount() {
        Axios.get('http://localhost:5000/books/'+this.props.match.params.id)
            .then(response => {
                    this.setState ({ book: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    bookList() {
        return this.state.book.map(currentbook => {
            return <Details book={currentbook} deleteBook={this.deleteBook} returnBook={this.returnBook} borrowBook={this.borrowBook} key={currentbook._id}/>;
        })
    }

    render() {
        return (
            <div>
                <p>This is the Book component! {window.location.href}</p>
            </div>
        )
    }
}
